import React, { useState, useEffect, useCallback } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Movies from './pages/Movies';
import TVSeries from './pages/TvSeries';
import TopIMDB from './pages/TopImDb';
import MovieDetail from './pages/MovieDetail';
import Profile from './pages/Profile';
import { AuthProvider } from './context/AuthContext';
import { movieAPI } from './services/api';
import './App.css';

function ScrollToTop() {
    const { pathname } = useLocation();

    useEffect(() => {
        window.scrollTo(0, 0);
    }, [pathname]);

    return null;
}

function App() {
    const [movies, setMovies] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');
    const [isSearching, setIsSearching] = useState(false);
    const [currentPage, setCurrentPage] = useState('home');

    // Wrap fetchMovies in useCallback to prevent infinite re-renders
    const fetchMovies = useCallback(async () => {
        try {
            setLoading(true);
            setError(null);
            console.log('Fetching popular movies...');

            const timeoutPromise = new Promise((_, reject) =>
                setTimeout(() => reject(new Error('Request timeout after 10 seconds')), 10000)
            );

            const dataPromise = movieAPI.getPopularMovies();
            const data = await Promise.race([dataPromise, timeoutPromise]);

            console.log('Movies data received:', data);

            if (data && data.results) {
                setMovies(data.results);
            } else {
                throw new Error('No movies data received from API');
            }

        } catch (error) {
            console.error('Error fetching movies:', error);
            setError(error.message);
            setMovies([]);
        } finally {
            setLoading(false);
        }
    }, []);

    const handleSearch = async (searchQuery) => {
        setSearchTerm(searchQuery);
        if (searchQuery.trim() === '') {
            setIsSearching(false);
            await fetchMovies();
        } else {
            setIsSearching(true);
            setLoading(true);
            try {
                const data = await movieAPI.searchMovies(searchQuery);
                setMovies(data.results || []);
            } catch (error) {
                setError('Search failed: ' + error.message);
                setMovies([]);
            } finally {
                setLoading(false);
            }
        }
    };

    const clearSearch = () => {
        setSearchTerm('');
        setIsSearching(false);
        fetchMovies();
    };

    const handlePageChange = (page) => {
        setCurrentPage(page);
    };


    useEffect(() => {
        const handlePopState = () => {
            if (!searchTerm && movies.length === 0) {
                fetchMovies();
            }
        };

        window.addEventListener('popstate', handlePopState);

        return () => {
            window.removeEventListener('popstate', handlePopState);
        };
    }, [searchTerm, movies.length, fetchMovies]);

    useEffect(() => {
        console.log('App mounted, fetching movies...');
        fetchMovies();
    }, [fetchMovies]);


    const homeProps = {
        movies,
        loading,
        error,
        searchTerm,
        fetchMovies,
        currentPage,
        onPageChange: handlePageChange
    };

    return (
        <Router>
            <AuthProvider>
                <div className="App">
                    <ScrollToTop />
                    <div className='navbar-container'>
                        <Navbar
                            searchTerm={searchTerm}
                            onSearchChange={handleSearch}
                            onClearSearch={clearSearch}
                            resultsCount={movies.length}
                            isSearching={isSearching}
                            currentPage={currentPage}
                            onPageChange={handlePageChange}
                        />
                    </div>

                    <Routes>
                        <Route path="/" element={<Home {...homeProps} />} />
                        <Route path="/movies" element={<Movies homeProps={homeProps} />} />
                        <Route path="/tv-series" element={<TVSeries homeProps={homeProps} />} />
                        <Route path="/top-imdb" element={<TopIMDB homeProps={homeProps} />} />
                        <Route path="/movie/:id" element={<MovieDetail homeProps={homeProps} />} />
                        <Route path="/profile" element={<Profile homeProps={homeProps} />} />
                        <Route path="*" element={<Home {...homeProps} />} />
                    </Routes>
                </div>
            </AuthProvider>
        </Router>
    );
}

export default App;
