import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Home from './Home';
import { movieAPI } from '../services/api';
import './MovieDetail.css';

const MovieDetail = ({ homeProps }) => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [movie, setMovie] = useState(null);
    const [credits, setCredits] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMovieData = async () => {
            try {
                setLoading(true);
                setError(null);

                // Fetch movie details and credits in parallel
                const [movieData, creditsData] = await Promise.all([
                    movieAPI.getMovieDetails(id),
                    movieAPI.getMovieCredits(id)
                ]);

                setMovie(movieData);
                setCredits(creditsData);
            } catch (err) {
                console.error('Error fetching movie data:', err);
                setError(err.message || 'Failed to load movie details');
            } finally {
                setLoading(false);
            }
        };

        if (id) {
            fetchMovieData();
        }
    }, [id]);

    const handleBackToHome = () => {
        navigate('/');
    };

    const handleBack = () => {
        navigate(-1); // Go back to previous page
    };

    // Format runtime
    const formatRuntime = (minutes) => {
        if (!minutes) return 'N/A';
        const hours = Math.floor(minutes / 60);
        const mins = minutes % 60;
        return `${hours}h ${mins}m`;
    };

    // Format currency
    const formatCurrency = (amount) => {
        if (!amount) return 'N/A';
        return new Intl.NumberFormat('en-US', {
            style: 'currency',
            currency: 'USD'
        }).format(amount);
    };

    // Get director from credits
    const getDirector = () => {
        if (!credits || !credits.crew) return 'N/A';
        const director = credits.crew.find(person => person.job === 'Director');
        return director ? director.name : 'N/A';
    };


    const getTopCast = () => {
        if (!credits || !credits.cast) return [];
        return credits.cast.slice(0, 6);
    };

    if (loading) {
        return (
            <div className="movie-detail-page">
                <div className="movie-detail-loading">
                    <div className="loading-spinner"></div>
                    <h2>Loading movie details...</h2>
                </div>
                <Home {...homeProps} />
            </div>
        );
    }

    if (error || !movie) {
        return (
            <div className="movie-detail-page">
                <div className="movie-detail-error">
                    <h2>Error Loading Movie</h2>
                    <p>{error || 'Movie not found'}</p>
                    <div className="error-actions">
                        <button onClick={handleBack} className="back-button">
                            ← Go Back
                        </button>
                        <button onClick={handleBackToHome} className="home-button">
                            Back to Home
                        </button>
                    </div>
                </div>
                <Home {...homeProps} />
            </div>
        );
    }

    return (
        <div className="movie-detail-page">
            <div className="movie-detail-content">

                <div className="movie-detail-nav">
                    <button onClick={handleBack} className="back-button">
                        ← Back
                    </button>
                    <button onClick={handleBackToHome} className="home-button">
                        Home
                    </button>
                </div>


                <div className="movie-hero">
                    <div className="movie-hero-backdrop">
                        {movie.backdrop_path && (
                            <img
                                src={`https://image.tmdb.org/t/p/w1280${movie.backdrop_path}`}
                                alt={movie.title}
                                className="backdrop-image"
                            />
                        )}
                        <div className="backdrop-overlay"></div>
                    </div>

                    <div className="movie-hero-content">
                        <div className="movie-poster">
                            {movie.poster_path ? (
                                <img
                                    src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                                    alt={movie.title}
                                    className="poster-image"
                                />
                            ) : (
                                <div className="poster-placeholder">No Image</div>
                            )}
                        </div>

                        <div className="movie-info">
                            <h1 className="movie-title">{movie.title}</h1>
                            {movie.original_title !== movie.title && (
                                <p className="movie-original-title">{movie.original_title}</p>
                            )}

                            <div className="movie-meta">
                                <span className="movie-year">
                                    {movie.release_date ? new Date(movie.release_date).getFullYear() : 'N/A'}
                                </span>
                                <span className="movie-runtime">
                                    {formatRuntime(movie.runtime)}
                                </span>
                                <span className="movie-rating">
                                    ⭐ {movie.vote_average ? movie.vote_average.toFixed(1) : 'N/A'} / 10
                                </span>
                                <span className="movie-votes">
                                    ({movie.vote_count ? movie.vote_count.toLocaleString() : 0} votes)
                                </span>
                            </div>

                            <div className="movie-genres">
                                {movie.genres && movie.genres.map(genre => (
                                    <span key={genre.id} className="genre-tag">
                                        {genre.name}
                                    </span>
                                ))}
                            </div>

                            <p className="movie-tagline">{movie.tagline}</p>

                            <div className="movie-overview">
                                <h3>Overview</h3>
                                <p>{movie.overview || 'No overview available.'}</p>
                            </div>

                            <div className="movie-details-grid">
                                <div className="detail-item">
                                    <strong>Director:</strong>
                                    <span>{getDirector()}</span>
                                </div>
                                <div className="detail-item">
                                    <strong>Budget:</strong>
                                    <span>{formatCurrency(movie.budget)}</span>
                                </div>
                                <div className="detail-item">
                                    <strong>Revenue:</strong>
                                    <span>{formatCurrency(movie.revenue)}</span>
                                </div>
                                <div className="detail-item">
                                    <strong>Status:</strong>
                                    <span>{movie.status || 'N/A'}</span>
                                </div>
                                <div className="detail-item">
                                    <strong>Original Language:</strong>
                                    <span>{movie.original_language ? movie.original_language.toUpperCase() : 'N/A'}</span>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>


                {credits && credits.cast && credits.cast.length > 0 && (
                    <div className="cast-section">
                        <h2>Top Cast</h2>
                        <div className="cast-grid">
                            {getTopCast().map(actor => (
                                <div key={actor.id} className="cast-card">
                                    <div className="cast-photo">
                                        {actor.profile_path ? (
                                            <img
                                                src={`https://image.tmdb.org/t/p/w200${actor.profile_path}`}
                                                alt={actor.name}
                                            />
                                        ) : (
                                            <div className="cast-placeholder">No Photo</div>
                                        )}
                                    </div>
                                    <div className="cast-info">
                                        <h4>{actor.name}</h4>
                                        <p>{actor.character}</p>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                )}


                <div className="additional-info">
                    <div className="info-section">
                        <h3>Production Companies</h3>
                        <div className="companies-list">
                            {movie.production_companies && movie.production_companies.map(company => (
                                <div key={company.id} className="company-item">
                                    {company.logo_path ? (
                                        <img
                                            src={`https://image.tmdb.org/t/p/w200${company.logo_path}`}
                                            alt={company.name}
                                            className="company-logo"
                                        />
                                    ) : (
                                        <span>{company.name}</span>
                                    )}
                                </div>
                            ))}
                        </div>
                    </div>

                    {movie.homepage && (
                        <div className="info-section">
                            <h3>Official Website</h3>
                            <a
                                href={movie.homepage}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="official-link"
                            >
                                Visit Official Website
                            </a>
                        </div>
                    )}
                </div>
            </div>


            <Home {...homeProps} />
        </div>
    );
};

export default MovieDetail;
