import React from 'react';
import { useNavigate } from 'react-router-dom';
import './MovieCard.css';

const MovieCard = ({ movie, isTvSeries = false }) => {
    const navigate = useNavigate();

    const handleCardClick = () => {
        if (movie && movie.id) {
            navigate(`/movie/${movie.id}`);
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter' || e.key === ' ') {
            e.preventDefault();
            handleCardClick();
        }
    };

    if (!movie) {
        return null;
    }

    const getReleaseYear = () => {
        const date = movie.release_date || movie.first_air_date;
        return date ? new Date(date).getFullYear() : 'N/A';
    };

    const getTitle = () => {
        return movie.title || movie.name || 'Unknown Title';
    };

    const getOverview = () => {
        return movie.overview || 'No description available.';
    };

    return (
        <div
            className="movie-card"
            onClick={handleCardClick}
            onKeyPress={handleKeyPress}
            tabIndex={0}
            role="button"
            aria-label={`View details for ${getTitle()}`}
        >
            <div className="movie-poster">
                {movie.poster_path ? (
                    <img
                        src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                        alt={getTitle()}
                        loading="lazy"
                    />
                ) : (
                    <div className="poster-placeholder">
                        No Image
                    </div>
                )}
                {isTvSeries && (
                    <div className="tv-series-badge">
                        TV
                    </div>
                )}
            </div>

            <div className="movie-info">
                <h3 className="movie-title">{getTitle()}</h3>

                <div className="movie-details">
                    <span className="movie-year">{getReleaseYear()}</span>
                    <div className="movie-rating">
                        ‚≠ê {movie.vote_average ? movie.vote_average.toFixed(1) : 'N/A'}
                    </div>
                </div>

                <p className="movie-overview">{getOverview()}</p>

                {movie.genre_names && movie.genre_names.length > 0 && (
                    <div className="movie-genres">
                        {movie.genre_names.slice(0, 2).map((genre, index) => (
                            <span key={index} className="genre-tag">
                                {genre}
                            </span>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default MovieCard;
