import React, { useState, useEffect, useCallback } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import ReviewForm from '../components/ReviewForm';
import { reviewAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import './MovieDetail.css';

const MovieDetail = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [movie, setMovie] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [cast, setCast] = useState([]);
    const [reviews, setReviews] = useState([]);
    const [reviewsLoading, setReviewsLoading] = useState(false);
    const [showReviewForm, setShowReviewForm] = useState(false);

    const { isAuthenticated } = useAuth();

    const API_KEY = '9bcdb1078fa24262529f44ab427f223e';



    const fetchMovieDetails = useCallback(async () => {
        try {
            setLoading(true);

            const movieResponse = await fetch(
                `https://api.themoviedb.org/3/movie/${id}?api_key=${API_KEY}&language=en-US`
            );

            if (!movieResponse.ok) {
                throw new Error('Failed to fetch movie details');
            }

            const movieData = await movieResponse.json();
            setMovie(movieData);

            const castResponse = await fetch(
                `https://api.themoviedb.org/3/movie/${id}/credits?api_key=${API_KEY}`
            );

            if (castResponse.ok) {
                const castData = await castResponse.json();
                setCast(castData.cast.slice(0, 10));
            }

        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    }, [id, API_KEY]);


    const fetchReviews = useCallback(async () => {
        try {
            setReviewsLoading(true);
            const reviewsData = await reviewAPI.getMovieReviews(id);
            setReviews(reviewsData.reviews || []);
        } catch (error) {
            console.error('Failed to fetch reviews:', error);
            setReviews([]);
        } finally {
            setReviewsLoading(false);
        }

    }, [id]);

    useEffect(() => {
        if (id) {
            fetchMovieDetails();
            fetchReviews();
        }
    }, [id, fetchMovieDetails, fetchReviews]);

    const handleReviewSubmit = (newReview) => {
        setReviews(prev => [newReview, ...prev]);
        setShowReviewForm(false);
        fetchReviews();
    };

    const handleAddReviewClick = () => {
        if (!isAuthenticated) {
            alert('Please log in to write a review');
            return;
        }
        setShowReviewForm(true);
    };

    const handleBackToHome = () => {
        navigate('/');
    };

    if (loading) {
        return (
            <div className="movie-detail-page">
                <div className="loading">
                    <h2>Loading Movie Details...</h2>
                    <div className="spinner"></div>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="movie-detail-page">
                <div className="error">
                    <h2>Something went wrong</h2>
                    <p>{error}</p>
                    <Link to="/" className="back-button">Back to Home</Link>
                </div>
            </div>
        );
    }

    if (!movie) {
        return (
            <div className="movie-detail-page">
                <div className="error">
                    <h2>Movie not found</h2>
                    <Link to="/" className="back-button">Back to Home</Link>
                </div>
            </div>
        );
    }

    const imageBaseUrl = "https://image.tmdb.org/t/p/w500";
    const backdropUrl = "https://image.tmdb.org/t/p/w1280";

    return (
        <div className="movie-detail-page">

            < div
                className="movie-backdrop"
                style={{
                    backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.9)), url(${backdropUrl}${movie.backdrop_path})`
                }}
            >
                <div className="backdrop-content">

                    <button
                        className="back-button"
                        onClick={handleBackToHome}
                    >
                        ‚Üê Back to Home
                    </button>

                    <div className="movie-hero">
                        <div className="poster-section">
                            <img
                                src={movie.poster_path ? `${imageBaseUrl}${movie.poster_path}` : '/placeholder-poster.jpg'}
                                alt={movie.title}
                                className="movie-poster-large"
                            />
                        </div>

                        <div className="movie-info">
                            <h1 className="movie-title">{movie.title}</h1>

                            <div className="movie-meta">
                                <span className="rating">
                                    ‚≠ê {movie.vote_average ? movie.vote_average.toFixed(1) : 'N/A'}/10
                                </span>
                                <span className="year">
                                    {movie.release_date ? new Date(movie.release_date).getFullYear() : 'TBA'}
                                </span>
                                <span className="runtime">
                                    {movie.runtime ? `${movie.runtime} min` : 'N/A'}
                                </span>
                            </div>

                            <div className="genres">
                                {movie.genres?.map(genre => (
                                    <span key={genre.id} className="genre-tag">{genre.name}</span>
                                ))}
                            </div>

                            <p className="movie-overview">{movie.overview}</p>

                            <div className="action-buttons">
                                <button className="watch-trailer">üé¨ Watch Trailer</button>
                                <button className="add-review"
                                    onClick={handleAddReviewClick}
                                >‚úçÔ∏è Write Review
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div >


            <div className="movie-details-content" >

                <section className="cast-section">
                    <h2>Cast</h2>
                    <div className="cast-grid">
                        {cast.map(person => (
                            <div key={person.id} className="cast-card">
                                <img
                                    src={person.profile_path ? `${imageBaseUrl}${person.profile_path}` : '/placeholder-avatar.jpg'}
                                    alt={person.name}
                                    className="cast-photo"
                                />
                                <div className="cast-info">
                                    <h4>{person.name}</h4>
                                    <p>{person.character}</p>
                                </div>
                            </div>
                        ))}
                    </div>
                </section>

                <section className="reviews-section">
                    <div className="reviews-header">
                        <h2>
                            Reviews
                            {reviews.length > 0 && ` (${reviews.length})`}
                        </h2>
                        <button className='add-review-btn' onClick={handleAddReviewClick}
                        >
                            + Add Review
                        </button>
                    </div>
                    {reviewsLoading ? (
                        <div className='loading'>
                            <div className='spinner'></div>
                            <p>Loading review...</p>
                        </div>
                    ) : reviews.length > 0 ? (
                        <div className='reviews-list'>
                            {reviews.map((review) => (
                                <div key={review.id} className='review-card'>
                                    <div className='review-header'>
                                        <h4>{review.title}</h4><div className='review-rating'>
                                            {'‚≠ê'.repeat(review.rating)}
                                            <span>({review.rating}/5)</span>
                                        </div>
                                    </div>
                                    <p className='review-meta'>
                                        By <strong>{review.username}</strong> ‚Ä¢ {new Date(review.created_at).toLocaleDateString()}
                                    </p>
                                    <p className='review-comment'>{review.comment}</p>
                                </div>
                            ))}
                        </div>
                    ) : (
                        <div className='reviews-placeholder'>
                            <p>No reviews yet.Be the first to share your thoughts!</p>
                            <button className='placeholder-review-btn' onClick={handleAddReviewClick}
                            >
                                Write First Review
                            </button>
                        </div>
                    )}
                </section>
            </div>

            {showReviewForm && (
                <ReviewForm
                    movie={movie}
                    onReviewSubmit={handleReviewSubmit}
                    onCancel={() => setShowReviewForm(false)}
                />
            )}
        </div>
    );
};

export default MovieDetail;
